mapper.select:
SELECT * FROM tb_borrow


      AND user_id = #{user_id}


      AND book_id = #{book_id}


    AND (SELECT category_id FROM tb_book WHERE tb_book.id = book_id LIMIT 1) = #{category_id}

mapper.update:
UPDATE tb_borrow


    user_id = #{user_id}


    book_id = #{book_id}


    borrow_date = #{borrow_date}


    return_date = #{return_date}


    really_return_date = #{really_return_date}


WHERE id = #{id}
