mapper.select:
SELECT * FROM tb_book WHERE 1 = 1
AND title LIKE CONCAT('%', #{title}, '%')
AND author LIKE CONCAT('%', #{author}, '%')
AND category_id = #{category_id}
AND publisher LIKE CONCAT('%', #{publisher}, '%')
ORDER BY id LIMIT #{startIndex}, #{pageSize}

mapper.update:
UPDATE tb_book
title = #{title}, author = #{author}, category_id = #{category_id}, publisher = #{publisher}, quantity = #{quantity}, created_at = #{created_at},
WHERE id = #{id}
